import React, { Component } from 'react';
import CounterButton from './components/counter-app/CounterButton';
import 'bootstrap/dist/css/bootstrap.css'; //https://create-react-app.dev/docs/adding-bootstrap/
// import logo from './logo.svg';
// import './App.css';

class App extends Component {
  render() {
    return (
      <div className="container">
        <CounterButton by = {+1}></CounterButton> {/*Increment the value of counter by 1. The value of by is obtained in CounterButton class using this.props.by*/}
        <CounterButton by = {5}></CounterButton> {/*Increment the value of counter by 5*/}
        <CounterButton by = {10}></CounterButton> {/*Increment the value of counter by 10*/}
        <CounterButton by = {-1}></CounterButton> {/*Decrement the value of counter by 1. The value of by is obtained in CounterButton class using this.props.by*/}
        <CounterButton by = {-5}></CounterButton> {/*Decrement the value of counter by 5*/}
        <CounterButton by = {-10}></CounterButton> {/*Decrement the value of counter by 10*/}
        <h5>Total Sum:</h5>
        <button className="btn btn-warning">Reset</button>
      </div>
    );
  }
}
export default App;
