import React, { Component } from 'react';
import CounterApp from './components/counter-app/CounterApp';
import 'bootstrap/dist/css/bootstrap.css'; //https://create-react-app.dev/docs/adding-bootstrap/
// import logo from './logo.svg';
// import './App.css';

class App extends Component {
  render() {
    return (
      <div className="container">
        <CounterApp by = {+1}></CounterApp> {/*Increment the value of counter by 1. The value of by is obtained in CounterApp class using this.props.by*/}
        <CounterApp by = {5}></CounterApp> {/*Increment the value of counter by 5*/}
        <CounterApp by = {10}></CounterApp> {/*Increment the value of counter by 10*/}
        <CounterApp by = {-1}></CounterApp> {/*Decrement the value of counter by 1. The value of by is obtained in CounterApp class using this.props.by*/}
        <CounterApp by = {-5}></CounterApp> {/*Decrement the value of counter by 5*/}
        <CounterApp by = {-10}></CounterApp> {/*Decrement the value of counter by 10*/}
        <h5>Total Sum:{this.state.totalCount}</h5>
        <button className="btn btn-warning" onClick={this.handleReset}>Reset</button>
      </div>
    );
  }

  handleReset(){
    // console.log("Reset Called");
    this.setState({
      totalCount: 0
    })
  }
}
export default App;
