import React, { Component } from 'react';
import CounterButton from './components/counter-app/CounterButton';
import 'bootstrap/dist/css/bootstrap.css'; //https://create-react-app.dev/docs/adding-bootstrap/
// import logo from './logo.svg';
// import './App.css';

class App extends Component {
  render() {
    return (
      <div className="container">
        <div className="row">
          <div className="row">
            <div className="col-1"><CounterButton by = {+1}></CounterButton> {/*Increment the value of counter by 1. The value of by is obtained in CounterButton class using this.props.by*/}</div>
            <div className="col-1"><CounterButton by = {-1}></CounterButton> {/*Decrement the value of counter by 1. The value of by is obtained in CounterButton class using this.props.by*/}</div>
          </div>

          <div className="row">
            <div className="col"><CounterButton by = {+5}></CounterButton> {/*Increment the value of counter by 5. The value of by is obtained in CounterButton class using this.props.by*/}</div>
            <div className="col"><CounterButton by = {-5}></CounterButton> {/*Decrement the value of counter by 5. The value of by is obtained in CounterButton class using this.props.by*/}</div>
          </div>

          <div className="row">
            <div className="col"><CounterButton by = {+10}></CounterButton> {/*Increment the value of counter by 10. The value of by is obtained in CounterButton class using this.props.by*/}</div>
            <div className="col"><CounterButton by = {-10}></CounterButton> {/*Decrement the value of counter by 10. The value of by is obtained in CounterButton class using this.props.by*/}</div>
          </div>
        </div>
      </div>
    );
  }
}
export default App;
