import React, { Component } from 'react';
import CounterButton from './CounterButton';

class CounterApp extends Component {
  constructor(){
    super();
    this.state={totalCount: 0,
    }
    this.handleReset = this.handleReset.bind(this);
  }
  render() {
    return (
      <div className="container">
        <CounterButton by = {+1} handleReset={this.handleReset}></CounterButton> {/*Increment the value of counter by 1. The value of by is obtained in CounterButton class using this.props.by*/}
        <CounterButton by = {5} handleReset={this.handleReset}></CounterButton> {/*Increment the value of counter by 5*/}
        <CounterButton by = {10} handleReset={this.handleReset}></CounterButton> {/*Increment the value of counter by 10*/}
        <CounterButton by = {-1} handleReset={this.handleReset}></CounterButton> {/*Decrement the value of counter by 1. The value of by is obtained in CounterButton class using this.props.by*/}
        <CounterButton by = {-5} handleReset={this.handleReset}></CounterButton> {/*Decrement the value of counter by 5*/}
        <CounterButton by = {-10} handleReset={this.handleReset}></CounterButton> {/*Decrement the value of counter by 10*/}
        <h5>Total Sum:{this.state.totalCount}</h5>
        <button className="btn btn-warning" onClick={this.handleReset}>Reset</button>
      </div>
    );
  }

  handleReset(val){
    // console.log("Reset Called");
    this.setState({
      totalCount: this.totalCount + val,
    })
  }
}
export default CounterApp;
